import React, { Component } from 'react';

class InputClient extends Component {
  constructor(props){
    super(props);

    this.state = {
      showMessage : false,
      name : '',
    };
  }
    handleClick(newColor){
      this.setState({
        ...this.state,
        buttonColor: newColor,
        showMessage : !this.state.showMessage,
      });
    }

}

