import React, { Component } from 'react';
import { breakfast } from "./breakfast.json";

function PrintBreakfast(props){
  return(
    <button className="buttonBreakfast" onClick={props.onClick}>DESAYUNO</button>
  );
};

class CreateBreakfast extends Component {
    constructor(props){
      super(props);
      this.state={
        breakfast: breakfast,
        printMenu:false,
      };
    }
    _printMenu = (bool) => {
      this.setState({
        printMenu: bool
      });
    }
    renderButton(i){
      return(
        <PrintBreakfast onClick= {()=> this.props.onClick(i)}/>
      );
    }
  
    render() {
  
    const menu = this.state.breakfast.map((food,i)=>{
      return(
        <div className="col-m d-4">
          <div className="card mt-4">
            <div className="card-header">
              <h3>{food.name}</h3>
            </div>
            <div className="card-body">
              <p>{food.price}</p>
            </div>
          </div>
        </div>
      )
    })
      return (
        <div className="menu">
          <div className="container">
            <div>
              <button onClick={this._prinTMenu.bind(null, true)}>DESAYUNO</button>
              { this.state.prinTMenu && {menu} }
            </div>
            <div className="row mt-4">
              {menu}
            </div>
          </div>
          
        </div>
      );
    }
  }
  export default CreateBreakfast;
